package designPattern.structurePattern.bridge;public class Retangle extends Shape {    private int width = 0;    private int heigth = 0;    public Retangle(DrawingProgramming dp, int width, int height) {        super(dp);        this.width = width;        this.heigth = height;    }    @Override    public void draw() {        this.drawRetangle(width, heigth);    }    public void drawRetangle(int width, int height) {        this.drawingProgramming.drawRetangle(width, height);    }    public int getWidth() {        return width;    }    public void setWidth(int width) {        this.width = width;    }    public int getHeigth() {        return heigth;    }    public void setHeigth(int heigth) {        this.heigth = heigth;    }}